// التعامل مع زر القائمة في الجوال
const navbarToggler = document.querySelector('.navbar-toggler');
const navbarCollapse = document.querySelector('.navbar-collapse');

navbarToggler.addEventListener('click', () => {
    navbarCollapse.classList.toggle('show');
    navbarToggler.classList.toggle('open');
});

// إعداد Google Translate
function googleTranslateElementInit() {
    new google.translate.TranslateElement({
        pageLanguage: 'en', // لغة الصفحة الأساسية
        includedLanguages: 'ar,en,fr,es,de', // اللغات المتاحة (يمكنك إضافة المزيد)
        layout: google.translate.TranslateElement.InlineLayout.SIMPLE
    }, 'google_translate_element');

    // تأكد من أن الزر يعمل بعد تهيئة Google Translate
    document.getElementById('translate-button').addEventListener('click', function() {
        var translateElement = document.getElementById('google_translate_element');
        if (translateElement.style.display === 'none' || translateElement.style.display === '') {
            translateElement.style.display = 'block';
        } else {
            translateElement.style.display = 'none';
        }
    });
}

AOS.init({
    duration: 1200, // مدة التحريك بالمللي ثانية
});
