
const translations = {
    en: {
        headerTitle: "Hello, I am Ahmed Khaled. How are you?",
        welcomeText: "Welcome to my personal portfolio website. I am a passionate web designer and developer based in Cairo, Egypt. Explore my projects, skills, and get in touch!"
    },
    ar: {
        headerTitle: "مرحبًا، أنا أحمد خالد. كيف حالك؟",
        welcomeText: "مرحبًا بك في موقع محفظتي الشخصية. أنا مصمم ومطور ويب شغوف من القاهرة، مصر. استكشف مشاريعي ومهاراتي وتواصل معي!"
    }
};

function toggleLanguage() {
    const currentLang = document.documentElement.lang;
    const newLang = currentLang === 'en' ? 'ar' : 'en';
    document.documentElement.lang = newLang;
    document.body.style.direction = newLang === 'ar' ? 'rtl' : 'ltr';

    document.getElementById('header-title').innerText = translations[newLang].headerTitle;
    document.getElementById('welcome-text').innerText = translations[newLang].welcomeText;
}

document.getElementById('contactForm').addEventListener('submit', function(e) {
    e.preventDefault();
    alert('Message sent successfully!');
});

// Lazy Loading للصور
document.addEventListener("DOMContentLoaded", function() {
    var lazyloadImages = document.querySelectorAll("img.lazyload");
    if ("IntersectionObserver" in window) {
        let imageObserver = new IntersectionObserver(function(entries, observer) {
            entries.forEach(function(entry) {
                if (entry.isIntersecting) {
                    let image = entry.target;
                    image.src = image.dataset.src;
                    image.classList.remove("lazyload");
                    imageObserver.unobserve(image);
                }
            });
        });

        lazyloadImages.forEach(function(image) {
            imageObserver.observe(image);
        });
    } else {
        let lazyloadThrottleTimeout;
        lazyloadImages = document.querySelectorAll("img.lazyload");

        function lazyload () {
            if(lazyloadThrottleTimeout) {
                clearTimeout(lazyloadThrottleTimeout);
            }

            lazyloadThrottleTimeout = setTimeout(function() {
                let scrollTop = window.pageYOffset;
                lazyloadImages.forEach(function(img) {
                    if(img.offsetTop < (window.innerHeight + scrollTop)) {
                        img.src = img.dataset.src;
                        img.classList.remove('lazyload');
                    }
                });
                if(lazyloadImages.length == 0) { 
                    document.removeEventListener("scroll", lazyload);
                    window.removeEventListener("resize", lazyload);
                    window.removeEventListener("orientationChange", lazyload);
                }
            }, 20);
        }

        document.addEventListener("scroll", lazyload);
        window.addEventListener("resize", lazyload);
        window.addEventListener("orientationChange", lazyload);
    }
});
